import React from "react";
import {
    Switch,
    Route,
} from "react-router-dom";

import Article from 'pages/article'
import GlobalFeed from 'pages/globalFeed'

export default () => {
    return (
        <Switch>
            <Route path="/" component={GlobalFeed} exact/>
            <Route path="/article/:slug" component={Article}/>
        </Switch>
    )
}